import React, {useState} from 'react'
import Notarea from './tareas'

const enviarTarea = (e, tarea, setTarea)=>{
    e.preventDefault()
    const id = (tarea.length) ? tarea[tarea.length - 1].id + 1 : 0
    console.log("Tarea.enviarTarea id", id)
    setTarea([...tarea, {id: id, nota: 'Nueva Tarea'}])
}

export default ()=>{
    const [tarea, setTarea] = useState([
        {id: 1, nota: "tarea"},
    ])
    const [input, setInput] = useState('')
    return(
        <div>
            <form onSubmit={(e)=> enviarTarea(e, tarea, setTarea, input, setInput)}>
                <input onChange={(e) => setInput(e.target.value)} value={input}/>
                <button>AÃ±adir</button>
            </form>
            {tarea.map(note =>(
                <Notarea nota={note.nota} id={note.id}/>
            ))}
        </div>
    )

}
