import React from 'react';
import '@fortawesome/fontawesome-free/css/all.css';
class App extends React.Component {
    constructor(props){
      super(props);

      this.state={
        tarea:""
      }
    }



  cambiarTarea(e) {
    this.setState({
      tarea: e.target.value
      })
  }

  deleteTarea(e) {
    this.setState({
      tarea: ""
      })
  }

  render(){
    return (
      <>
        <div className="libreta">
          <div className="dib">
              <input className="nota" placeholder="AÃ±ade una tarea" onKeyUp={
                      (evento) => {
                          this.cambiarTarea(evento);
                          console.log(this.state.tarea);
                      }
                  } 

                  onBlur={
                      (evento) => {
                          console.log(this.state.tarea);
                      }
                  } 
                  /> 
              <button className="fas fa-trash-alt trash"
              onClick={
                (evento) => {
                  this.deleteTarea(evento);
                }
            }

            onMouseOut={
              (evento) => {
                  console.log(this.state.tarea);
              }
          }
              />
          </div> 
        </div>
      </>
    );
  }
}

export default App;
